import React, { useState } from 'react';
import logincss from '../assets/css/login.css';
import Mainlayout from '../layout/mainlayout';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';

function Login() {
    // Step 1: Define state to manage the active panel
    const [isRightPanelActive, setIsRightPanelActive] = useState(false);

    const [name, setName] = useState()
    const [email, setEmail] = useState()
    const [mobile, setMo] = useState()
    const [address, setAddress] = useState()
    const [password, setPassword] = useState()
    
    const [emaillog, Emaillogin] = useState()
    const [passwordlog, passwordlogin] = useState()

    const handleSubmitlogin = (e) => {
        e.preventDefault()
        axios.post('http://localhost:3001/logister', {emaillog,passwordlog})
        .then(result =>{
            console.log(result)
            window.location.reload();
            if(result.data === "Success"){
                navigator('/')
            }
        })
        .catch(err=> console.log(err))
    }
    return (
        <Mainlayout>
            <div>
                <head>
                    <meta charSet="UTF-8" />
                    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                    <title>Login</title>
                    <link rel="stylesheet" href={logincss} />
                </head>
                <body id='loginbody'>
                    <div id="container" className={`${isRightPanelActive ? 'right-panel-active' : ''}`}>
                        <div className="form-container sign-in-container">
                            <form className="frm" onSubmit={handleSubmitlogin}>
                                <h1 className="h1">Sign in</h1>
                                <input className="inp" type="email" placeholder="Email" onChange={(e)=>Emaillogin(e.target.value)} />
                                <input className="inp" type="password" placeholder="Password" onChange={(e)=>passwordlogin(e.target.value)} />
                                <a id='ancor' href="#">Forgot your password?</a>
                                <button className="lgbtn">Sign In</button>
                            </form>
                        </div>
                        <div className="overlay-container">
                            <div className="overlay">
                                <div className="overlay-panel overlay-right">
                                    <h1 className="h1 hello">Hello, Friend!</h1>
                                    <p className="para">New user signUp here</p>
                                    <button className="ghost lgbtn" id="signUp" >Sign Up</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </body >
            </div >
        </Mainlayout>
    );
}

export default Login;
