import React, { useState } from 'react';
import logincss from '../assets/css/login.css';
import Mainlayout from '../layout/mainlayout';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory hook

function Logister() {
    const [isRightPanelActive, setIsRightPanelActive] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [mobile, setMobile] = useState('');
    const [address, setAddress] = useState('');
    const [password, setPassword] = useState('');
    
    const history = useHistory(); // Initialize useHistory

    const handleSignUp = () => {
        setIsRightPanelActive(true);
        history.push('/signup'); // Navigate to /signup when signing up
    };

    const handleSignIn = () => {
        setIsRightPanelActive(false);
        history.push('/login'); // Navigate to /login when signing in
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const userData = { name, email, mobile, address, password };
        axios.post('http://localhost:3001/logister', userData)
            .then(result => {
                console.log(result);
                // Redirect to another page on successful signup
                // Example: history.push('/seminars');
            })
            .catch(err => console.error(err));
    };

    return (
        <Mainlayout>
            <div>
                <head>
                    <meta charSet="UTF-8" />
                    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                    <title>Login</title>
                    <link rel="stylesheet" href={logincss} />
                </head>
                <body id='loginbody'>
                    <div id="container" className={`${isRightPanelActive ? 'right-panel-active' : ''}`}>
                        <div className="form-container sign-up-container">
                            <form className="frm" onSubmit={handleSubmit}>
                                <h1 className="h1" id='titleregi'>Create Account</h1>
                                <input className="inp" type="text" placeholder="Full Name" value={name} onChange={(e) => setName(e.target.value)} />
                                <input className="inp" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
                                <input className="inp" type="text" placeholder="Mobile No" value={mobile} onChange={(e) => setMobile(e.target.value)} />
                                <input className="inp" type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} />
                                <input className="inp" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                                <input className="inp" type="password" placeholder="Confirm Password" />
                                <button className="lgbtn" onClick={handleSignUp}>Sign Up</button>
                            </form>
                        </div>
                        <div className="form-container sign-in-container">
                            <form action="#" className="frm">
                                <h1 className="h1">Sign in</h1>
                                <input className="inp" type="email" placeholder="Email" />
                                <input className="inp" type="password" placeholder="Password" />
                                <a id='ancor' href="#">Forgot your password?</a>
                                <button className="lgbtn" onClick={handleSignIn}>Sign In</button>
                            </form>
                        </div>
                        <div className="overlay-container">
                            <div className="overlay">
                                <div className="overlay-panel overlay-left">
                                    <h1 className="h1 hello">Welcome Back!</h1>
                                    <p className="para">To keep connected with us please login with your personal info</p>
                                    <button className="ghost lgbtn" id="signIn" onClick={handleSignIn}>Sign In</button>
                                </div>
                                <div className="overlay-panel overlay-right">
                                    <h1 className="h1 hello">Hello, Friend!</h1>
                                    <p className="para">New user signUp here</p>
                                    <button className="ghost lgbtn" id="signUp" onClick={handleSignUp}>Sign Up</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </body>
            </div>
        </Mainlayout>
    );
}

export default Logister;
