import React, { useState } from 'react';
import logincss from '../assets/css/login.css';
import Mainlayout from '../layout/mainlayout';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom'


function Login() {

    const [email, Emaillogin] = useState()
    const [password, passwordlogin] = useState()
    const navigate=useNavigate()


    const handleSubmitlogin = (e) => {
        if(email==''){
            console.log("Enter mail");
        }
        e.preventDefault()
        axios.post('http://localhost:3001/login', {email,password})
        .then(result =>{
            console.log(result)
            // window.location.reload();
            if(result.data === "Success"){
                navigate('/')
            }
            else{
                {alert(result.data)}
            }
        })
        .catch(err=> console.log(err))
    }
    return (
        <Mainlayout>
            <div>
                <head>
                    <meta charSet="UTF-8" />
                    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                    <title>Login</title>
                    <link rel="stylesheet" href={logincss} />
                </head>
                <body id='loginbody'>
                    <div id="container">
                        <div className="form-container sign-in-container">
                            <form className="frm" onSubmit={handleSubmitlogin}>
                                <h1 className="h1">Login</h1>
                                <input className="inp" type="email" placeholder="Email" onChange={(e)=>Emaillogin(e.target.value)} />
                                <input className="inp" type="password" placeholder="Password" onChange={(e)=>passwordlogin(e.target.value)} />
                                <a id='ancor' href="#">Forgot your password?</a>
                                <button className="lgbtn">login</button>
                            </form>
                        </div>
                        <div className="overlay-container">
                            <div className="overlay">
                                <div className="overlay-panel overlay-right">
                                    <h1 className="h1 hello">Hello, Friend!</h1>
                                    <p className="para">New user create account from here</p>
                                    <Link to="/register"><button className="ghost lgbtn" id="signUp" >Sign Up</button></Link>
                                </div>
                            </div>
                        </div>
                    </div>
                </body >
            </div >
        </Mainlayout>
    );
}

export default Login;
