import React, { useEffect, useState } from 'react';
import logincss from '../assets/css/login.css';

function Login() {

    const handleSignUp = () => {
        const container = document.getElementById('container');
        container.className.add("right-panel-active");
    };

    const handleSignIn = () => {
        const container = document.getElementById('container');
        container.className.remove("right-panel-active");
    };

    return (
        <div>
            <head>
                <meta charSet="UTF-8" />
                <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                <title>Login</title>
                <link rel="stylesheet" href={logincss} />
            </head>
            <body id='loginbody'>
                <div className="containerlogin" id="container">
                    <div className="form-container sign-up-container">
                        <form action="#" className="frm">
                            <h1 className="h1">Create Account</h1>
                            <input className="inp" type="text" placeholder="Name" />
                            <input className="inp" type="email" placeholder="Email" />
                            <input className="inp" type="password" placeholder="Password" />
                            <button className="lgbtn">Sign Up</button>
                        </form>
                    </div>
                    <div className="form-container sign-in-container">
                        <form action="#" className="frm">
                            <h1 className="h1">Sign in</h1>
                            <input className="inp" type="email" placeholder="Email" />
                            <input className="inp" type="password" placeholder="Password" />
                            <a id='ancor' href="#">Forgot your password?</a>
                            <button className="lgbtn">Sign In</button>
                        </form>
                    </div>
                    <div className="overlay-container">
                        <div className="overlay">
                            <div className="overlay-panel overlay-left">
                                <h1 className="h1">Welcome Back!</h1>
                                <p className="para">To keep connected with us please login with your personal info</p>
                                <button className="ghost lgbtn" id="signIn" onClick={handleSignUp}>Sign In</button>
                            </div>
                            <div className="overlay-panel overlay-right">
                                <h1 className="h1">Hello, Friend!</h1>
                                <p className="para">Enter your personal details and start journey with us</p>
                                <button className="ghost lgbtn" id="signUp" onClick={handleSignIn}>Sign Up</button>
                            </div>
                        </div>
                    </div>
                </div>
            </body >
        </div >
    );
}

export default Login;
