import React, { useState } from 'react';
import logincss from '../assets/css/login.css';
import Mainlayout from '../layout/mainlayout';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom'


function Register() {
    const [name, setName] = useState()
    const [email, setEmail] = useState()
    const [mobile, setMo] = useState()
    const [address, setAddress] = useState()
    const [password, setPassword] = useState()
    const [cpassword, setcPassword] = useState()
    const navigate=useNavigate()
    
    const handleSubmit = (e) => {
        if(!name || !email || !mobile || !address || !password || !cpassword){
            {alert("Enter require information")}
        }
        else{
            // e.preventDefault()
            axios.post('http://localhost:3001/register', {name,email,mobile,address,password})
            .then(result =>{
                console.log(result)
                navigate('/login')
            })
            .catch(err=> console.log(err))
        }
    }
    
    return (
        <Mainlayout>
            <div>
                <head>
                    <meta charSet="UTF-8" />
                    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                    <title>Login</title>
                    <link rel="stylesheet" href={logincss} />
                </head>
                <body id='loginbody'>
                    <div id="container" className="right-panel-active">
                        <div className="form-container sign-up-container">
                            <form className="frm" onSubmit={handleSubmit}>
                                <h1 className="h1" id='titleregi'>Create Account</h1>
                                <input className="inp" type="text" placeholder="Full Name" onChange={(e)=>setName(e.target.value)}/>
                                <input className="inp" type="email" placeholder="Email" onChange={(e)=>setEmail(e.target.value)} />
                                <input className="inp" type="text" placeholder="Mobile No" onChange={(e)=>setMo(e.target.value)} />
                                <input className="inp" type="text" placeholder="Address" onChange={(e)=>setAddress(e.target.value)} />
                                <input className="inp" type="password" placeholder="Password" onChange={(e)=>setPassword(e.target.value)} />
                                <input className="inp" type="password" placeholder="Confirm Password" onChange={(e)=>setcPassword(e.target.value)} />
                                <button className="lgbtn">Register</button>
                            </form>
                        </div>
                        <div className="overlay-container">
                            <div className="overlay">
                                <div className="overlay-panel overlay-left">
                                    <h1 className="h1 hello">Welcome Back!</h1>
                                    <p className="para">Already have an account login here</p>
                                    <Link to="/login"><button className="ghost lgbtn" id="signIn">Sign In</button></Link>
                                </div>
                            </div>
                        </div>
                    </div>
                </body >
            </div >
        </Mainlayout>
    );
}

export default Register;
