import React, { useState } from 'react';
import logincss from '../assets/css/login.css';
import Mainlayout from '../layout/mainlayout';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom'
import { ToastContainer, toast } from 'react-toastify';


function Login() {
    const [logininfo, setlogininfo] = useState({
        name: '',
        email: '',
        mobile: '',
        address: '',
        password: '',
        cpassword: '',
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRegisterInfo({ ...registerinfo, [name]: value });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        const { name, email, mobile, address, password, cpassword } = registerinfo;

        if (!name || !email || !mobile || !address || !password || !cpassword) {
            return handleError('All fields are required');
        }

        if (password !== cpassword) {
            return handleError('Passwords do not match');
        }
        
        try {
            const url = "http://localhost:8080/auth/register";
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-type':'application/json'
                },
                body:JSON.stringify(registerinfo)
            });
            const result = await response.json();
            const {success,message,error}=result;
            console.log(result);
            if(success){
                handleSuccess(message);
                setTimeout(() => {
                    navigate('/login');
                }, 2000);
            }
            else if (error) {
                // const details = error?.details[0].message;
                handleError(error);
            }
            else if (!success) {
                handleError(message);
            }
        } catch (error) {
            handleError(error);
        }
    }

    return (
        <Mainlayout>
            <div>
                <head>
                    <meta charSet="UTF-8" />
                    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
                    <title>Login</title>
                    <link rel="stylesheet" href={logincss} />
                </head>
                <body id='loginbody'>
                    <div id="container">
                        <div className="form-container sign-in-container">
                            <form className="frm" onSubmit={handleSubmit}>
                                <h1 className="h1">Login</h1>
                                <input className="inp" type="email" value={registerinfo.email} onChange={handleChange} placeholder="Email" />
                                <input className="inp" type="password" value={registerinfo.password} onChange={handleChange} placeholder="Password" />
                                <a id='ancor' href="#">Forgot your password?</a>
                                <button className="lgbtn">login</button>
                            </form>
                        </div>
                        <ToastContainer/>
                        <div className="overlay-container">
                            <div className="overlay">
                                <div className="overlay-panel overlay-right">
                                    <h1 className="h1 hello">Hello, Friend!</h1>
                                    <p className="para">New user create account from here</p>
                                    <Link to="/register"><button className="ghost lgbtn" id="signUp" >Sign Up</button></Link>
                                </div>
                            </div>
                        </div>
                    </div>
                </body >
            </div >
        </Mainlayout>
    );
}

export default Login;
